updates.takeSupportChat:
function(doc, req) {
	if (!doc || doc.type !== 'supportChat') {
		return [null, '{ "ok": false, "reason": "missed doc" }'];
	}
	if (!req.body) {
		return [null, '{ "ok": false, "reason": "missed body" }'];
	}
	var body = JSON.parse(req.body);
	if (doc.closeDate !== '') {
    return [null, '{ "ok": false, "reason": "already closed" }'];
  }
	if (doc.did !== '') {
		return [null, '{ "ok": false, "reason": "already taken" }'];
	}
  doc.did = body.did;
	doc.meta.closedBy = 'operator';
  return [doc, '{ "ok": true }'];
}

updates.updMessageStatus:
function(doc, req) {
	if (!doc || doc.type !== 'supportMessage') {
		return [null, '{ "ok": false, "reason": "missed doc" }'];
	}
	if (!req.body) {
		return [null, '{ "ok": false, "reason": "missed body" }'];
	}
	var body = JSON.parse(req.body);
	if (doc.to !== body.to) {
		return [null, '{ "ok": false, "reason": "access deny" }'];
	}
	if (body.deliveredDate) {
		if (doc.deliveredDate !== '') {
    	return [null, '{ "ok": false, "reason": "already delivered" }'];
  	}
		doc.deliveredDate = body.deliveredDate;
	}
	if (body.readDate) {
		if (doc.readDate !== '') {
    	return [null, '{ "ok": false, "reason": "already read" }'];
  	}
		doc.readDate = body.readDate;
	}
	return [doc, '{ "ok": true, "from": "'+doc.from+'", "chatId": "'+doc.chatId+'" }'];
}

updates.closeSupportChat:
function(doc, req) {
	if (!doc || doc.type !== 'supportChat') {
		return [null, '{ "ok": false, "reason": "missed doc" }'];
	}
	if (!req.body) {
		return [null, '{ "ok": false, "reason": "missed body" }'];
	}
	var body = JSON.parse(req.body);
	if (body.userId !== doc.pid && body.userId !== doc.did) {
		return [null, '{ "ok": false, "reason": "access deny" }'];
	}
	if (doc.closeDate !== '') {
    return [null, '{ "ok": false, "reason": "already closed" }'];
  }
	if (body.userId === doc.pid) {
		doc.closeDate = new Date();
		doc.meta.closedBy = 'patient';
	}
	if (body.userId === doc.did) {
		doc.closeDate = new Date();
		doc.meta.closedBy = 'operator';

	}
	return [doc, JSON.stringify({ ok: true, did: doc.did, pid: doc.pid, title: doc.title, closeDate: doc.closeDate, meta: doc.meta })];
}
