patientLoginPassword.map:
function(doc) {
  if (doc.class_name === 'cat.patients') {
    doc.contactInformation.forEach(function(contact) {
      if(contact.kind.presentation === 'Телефон') emit([contact.phoneNumber, doc.password], doc);
      if(contact.kind.presentation === 'E-mail') emit([contact.emailAddress, doc.password], doc);
    });
  }
}

statusesOfAnalysis.map:
function (doc) {
  if(doc.class_name === 'ireg.statusesOfAnalysis') {
    emit([doc.document.ref, doc.stringGUID], {stringGUID: doc.stringGUID, period: doc.period, status: doc.status.name});
  }
}

statusesOfAnalysis.reduce:
function (keys, values, rereduce) {
  var latestDoc = {};
  latestDoc.period = "0000-00-00T00:00:00";
  values.forEach(function(doc){
    if(doc.period > latestDoc.period) {
      latestDoc = doc;
    }
  });
  return latestDoc;
}
